// src/elements/Tasks.js
import React, { useEffect, useState } from 'react';
import {
  createTask,
  getTasks,
  updateTask,
  deleteTask,
  completeTask,
  getUsers
} from '../api';

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '', categoryId: '', dueDate: '', priority: 'LOW', owner: '', assignees: [] });
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchTasks();
    fetchUsers();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await getTasks();
      setTasks(response.data);
    } catch (error) {
      console.error('There was an error fetching the tasks!', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await getUsers();
      setUsers(response.data);
    } catch (error) {
      console.error('There was an error fetching the users!', error);
    }
  };

  const loadOptions = async (inputValue) => {
    try {
      const response = await getUsers(); // Fetch users from the API
      const filteredUsers = response.data
        .filter(user => user.username.toLowerCase().includes(inputValue.toLowerCase()))
        .map(user => ({ value: user.id, label: user.username }));
      return filteredUsers;
    } catch (error) {
      console.error('There was an error loading the options!', error);
      return [];
    }
  };

  const handleCreateTask = async () => {
    try {
      const taskData = {
        title: newTask.title,
        description: newTask.description,
        categoryId: parseInt(newTask.categoryId, 10), // Ensure categoryId is a number
        dueDate: new Date(newTask.dueDate).toISOString(), // Ensure dueDate is in ISO format
        priority: newTask.priority,
        owner: parseInt(newTask.owner, 10), // Ensure owner is a number
        assignees: newTask.assignees.map(assignee => parseInt(assignee, 10)) // Ensure assignees are numbers
      };

      const response = await createTask(taskData);
      setTasks([...tasks, response.data]);
      setNewTask({ title: '', description: '', categoryId: '', dueDate: '', priority: 'LOW', owner: '', assignees: [] });
    } catch (error) {
      console.error('There was an error creating the task!', error);
    }
  };

  const handleUpdateTask = async (taskId, updatedTask) => {
    try {
      const response = await updateTask(taskId, updatedTask);
      setTasks(tasks.map(task => (task.id === taskId ? response.data : task)));
    } catch (error) {
      console.error('There was an error updating the task!', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId);
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (error) {
      console.error('There was an error deleting the task!', error);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      await completeTask(taskId);
      setTasks(tasks.map(task => (task.id === taskId ? { ...task, status: 'COMPLETED' } : task)));
    } catch (error) {
      console.error('There was an error completing the task!', error);
    }
  };

  return (
    <div>
      <h2>Tasks</h2>
      <input
        type="text"
        value={newTask.title}
        onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
        placeholder="New Task Title"
      />
      <input
        type="text"
        value={newTask.description}
        onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
        placeholder="New Task Description"
      />
      <input
        type="text"
        value={newTask.categoryId}
        onChange={(e) => setNewTask({ ...newTask, categoryId: e.target.value })}
        placeholder="Category ID"
      />
      <input
        type="datetime-local"
        value={newTask.dueDate}
        onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
        placeholder="Due Date"
      />
      <select
        value={newTask.priority}
        onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
      >
        <option value="LOW">Low</option>
        <option value="MEDIUM">Medium</option>
        <option value="HIGH">High</option>
      </select>
      <input
        type="text"
        value={newTask.owner}
        onChange={(e) => setNewTask({ ...newTask, owner: e.target.value })}
        placeholder="Owner ID"
      />
      <input
        type="text"
        value={newTask.assignees.join(',')}
        onChange={(e) => setNewTask({ ...newTask, assignees: e.target.value.split(',') })}
        placeholder="Assignee IDs (comma separated)"
      />
      <button onClick={handleCreateTask}>Create Task</button>
      <ul>
        {tasks.map(task => (
          <li key={task.id}>
            {task.title} - {task.description} - {task.status}
            <button onClick={() => handleUpdateTask(task.id, { ...task, title: prompt('New title:', task.title) })}>Edit</button>
            <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
            <button onClick={() => handleCompleteTask(task.id)}>Complete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tasks;
