import React, { useEffect, useState, useCallback } from 'react';
import {
    getTaskComments,
    addTaskComment,
    getUser // Assuming you have an API function to get user by username
} from '../api';
import '../style/Comments.css'; // Assuming you have a CSS file for styling

const Comment = ({ taskId, username }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState({ content: '', author: username, timestamp: null });
    const [error, setError] = useState('');
    const [author, setAuthor] = useState(null);

    const fetchComments = useCallback(async () => {
        try {
            const response = await getTaskComments(taskId);
            setComments(response.data || []);
        } catch (error) {
            console.error('There was an error fetching the comments!', error);
        }
    }, [taskId]);

    const fetchUserId = useCallback(async () => {
        try {
            const response = await getUser(username);
            setAuthor(response.data.id);
        } catch (error) {
            console.error('There was an error fetching the user ID!', error);
        }
    }, [username]);

    useEffect(() => {
        fetchComments();
        fetchUserId();
    }, [fetchComments, fetchUserId]);

    const handleCreateComment = async () => {
        if (!newComment.content.trim()) {
            setError('Content is mandatory.');
            return;
        }

        if (!taskId) {
            setError('Task ID is mandatory.');
            return;
        }

        if (!author) {
            setError('Author is mandatory.');
            return;
        }

        const now = new Date();
        const commentToCreate = {
            ...newComment,
            taskId,
            author,
            timestamp: [
            now.getUTCFullYear(),
            now.getUTCMonth() + 1, // Months are zero-based in JavaScript
            now.getUTCDate(),
            now.getUTCHours(),
            now.getUTCMinutes(),
            now.getUTCSeconds(),
            now.getUTCMilliseconds() * 1000 // Convert milliseconds to nanoseconds
            ]
        };

        try {
            const response = await addTaskComment(taskId, commentToCreate);
            setComments([...comments, response.data]);
            setNewComment({ content: '', author: username, timestamp: null });
            setError('');
        } catch (error) {
            console.error('There was an error creating the comment!', error);
            console.error(error.response.data);
        }
    };

    return (
        <div className="comment-container">
            <h2>Comments</h2>
            <div className="new-comment">
                <textarea
                    value={newComment.content}
                    onChange={(e) => setNewComment({ ...newComment, content: e.target.value })}
                    placeholder="New Comment"
                />
                <button onClick={handleCreateComment}>Add Comment</button>
                {error && <p className="error">{error}</p>}
            </div>
            <ul className="comments-list">
                {comments.map(comment => (
                    <li key={comment.id} className="comment-item">
                        <span className="comment-author">{comment.author}</span>
                        <span className="comment-content">{comment.content}</span>
                        <span className="comment-timestamp">
                            {new Date(Date.UTC(comment.timestamp[0], comment.timestamp[1] - 1, comment.timestamp[2], comment.timestamp[3], comment.timestamp[4], comment.timestamp[5])).toLocaleString('en-US', {
                                year: 'numeric',
                                day: 'numeric',
                                month: 'long'
                            })}
                        </span>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Comment;
