// src/elements/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { loginUser, getUser } from '../api';
import './Login.css'; // Assuming you have a CSS file for styling

const Login = () => {
  const [user, setUser] = useState({ username: '', password: '' });
  const [agree, setAgree] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!agree) {
      alert('You must agree to the Privacy Policy.');
      return;
    }
    try {
      const response = await loginUser(user);
      localStorage.setItem('token', response.data.token);
      setUser({ username: '', password: '' }); // Clear form
      navigate('/'); // Redirect to dashboard after successful login
      login(await getUser(user.username)); // Set authenticated state
    } catch (error) {
      console.error('Login failed', error);
    }
  };

  return (
    <div className="login-container">
      <h2>Task Master</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={user.username}
            onChange={(e) => setUser({ ...user, username: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={user.password}
            onChange={(e) => setUser({ ...user, password: e.target.value })}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="checkbox"
            id="agree"
            checked={agree}
            onChange={(e) => setAgree(e.target.checked)}
            required
          />
          <label htmlFor="agree">
            I agree to the <a href="/privacypolicy">Privacy Policy</a>.
          </label>
        </div>
        <button className="login-button" type="submit">Log In</button>
        <div className="form-group">
          <label className="no-account-label">Don't have an account?</label>
          <a href="/register">
            <button className="register-button" type="button">Create an account today!</button>
          </a>
        </div>
      </form>
    </div>
  );
};

export default Login;
