import React, { useState, useEffect } from 'react';
import { getUser, updateUser } from '../api';
import { useAuth } from '../context/AuthContext';

const Settings = () => {
  const { currentUsername } = useAuth();
  const [user, setUser] = useState({ notificationsEnabled: false });

  useEffect(() => {
    fetchUserSettings();
  }, []);

  const fetchUserSettings = async () => {
    try {
      if (currentUsername) {
        const response = await getUser(currentUsername); // Adjust the endpoint as needed
        setUser(response.data);
      }
    } catch (error) {
      console.error('Error fetching user settings', error);
    }
  };

  const handleToggleNotifications = () => {
    setUser({ ...user, notificationsEnabled: !user.notificationsEnabled });
  };

  const handleSaveSettings = async () => {
    try {
      if (currentUsername) {
        await updateUser(currentUsername, user); // Adjust the endpoint as needed
        alert('Settings saved successfully');
      }
    } catch (error) {
      console.error('Error saving settings', error);
    }
  };

  return (
    <div>
      <h2>Settings</h2>
      <label>
        Notifications Enabled:
        <input
          type="checkbox"
          checked={user.notificationsEnabled}
          onChange={handleToggleNotifications}
        />
      </label>
      <button onClick={handleSaveSettings}>Save</button>
    </div>
  );
};

export default Settings;
