import React, { useEffect, useState } from 'react';
import {
  getCategory,
  createTask,
  getTasks,
  updateTask,
  deleteTask,
  completeTask,
  getCategories,
  getUsers,
  getUser
} from '../api';
import Comment from './Comments';
import Reminder from './Reminders';
import './Tasks.css'; // Assuming you have a CSS file for styling
import { useAuth } from '../context/AuthContext';
import AsyncSelect from 'react-select/async';

const Tasks = () => {
  const { currentUsername } = useAuth();
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [users, setUsers] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '', categoryId: '', dueDate: '', priority: 'LOW', owner: '', assignees: [], status: 'PENDING' });
  const [editingTask, setEditingTask] = useState(null);
  const [selectedTaskForComments, setSelectedTaskForComments] = useState(null);
  const [selectedTaskForReminders, setSelectedTaskForReminders] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await getUser(currentUsername);
        setUser(response.data);
      } catch (error) {
        console.error('There was an error fetching the user!', error);
      }
    };

    if (currentUsername) {
      fetchUser();
    }
  }, [currentUsername]);

  useEffect(() => {
    fetchTasks();
    fetchCategories();
    fetchUsers();
    if (user) {
      setNewTask(prevTask => ({ ...prevTask, owner: user.id }));
    }
  }, [user]);

  const fetchTasks = async () => {
    try {
      const response = await getTasks();
      setTasks(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      console.error('There was an error fetching the tasks!', error);
      setTasks([]);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await getCategories();
      setCategories(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      console.error('There was an error fetching the categories!', error);
      setCategories([]);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await getUsers();
      setUsers(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      console.error('There was an error fetching the users!', error);
      setUsers([]);
    }
  };

  const loadOptions = async (inputValue) => {
    try {
      const response = await getUsers();
      const filteredUsers = response.data
        .filter(user => user.username.toLowerCase().includes(inputValue.toLowerCase()))
        .map(user => ({ value: user.id, label: user.username }));
      return filteredUsers;
    } catch (error) {
      console.error('There was an error loading the options!', error);
      return [];
    }
  };

  const isValidDate = (dateString) => {
    const date = new Date(dateString);
    return !isNaN(date.getTime());
  };

  const handleCreateTask = async () => {
    try {
      const categoryResponse = await getCategory(newTask.categoryId);
      const category = categoryResponse.data;

      const taskData = {
        title: newTask.title,
        description: newTask.description,
        category: { id: category.id, name: category.name },
        dueDate: isValidDate(newTask.dueDate) ? new Date(newTask.dueDate).toISOString() : null,
        priority: newTask.priority,
        owner: newTask.owner,
        assignees: newTask.assignees.map(assignee => parseInt(assignee, 10)),
        status: newTask.status
      };

      const response = await createTask(taskData);
      setTasks([...tasks, response.data]);
      setNewTask({ title: '', description: '', categoryId: '', dueDate: '', priority: 'LOW', owner: '', assignees: [], status: 'PENDING' });
    } catch (error) {
      console.error('There was an error creating the task!', error);
    }
  };

  const handleUpdateTask = async () => {
    try {
      const categoryResponse = await getCategory(editingTask.categoryId);
      const category = categoryResponse.data;

      const taskData = {
        ...editingTask,
        category: { id: category.id, name: category.name },
        dueDate: isValidDate(editingTask.dueDate) ? new Date(editingTask.dueDate).toISOString() : null,
        assignees: editingTask.assignees.map(assignee => parseInt(assignee, 10))
      };

      const response = await updateTask(editingTask.id, taskData);
      setTasks(tasks.map(task => (task.id === editingTask.id ? response.data : task)));
      setEditingTask(null);
    } catch (error) {
      console.error('There was an error updating the task!', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId);
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (error) {
      console.error('There was an error deleting the task!', error);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      await completeTask(taskId);
      setTasks(tasks.map(task => (task.id === taskId ? { ...task, status: 'COMPLETED' } : task)));
    } catch (error) {
      console.error('There was an error completing the task!', error);
    }
  };

  const handleInProgressTask = async (taskId) => {
    try {
      await updateTask(taskId, { status: 'IN_PROGRESS' });
      setTasks(tasks.map(task => (task.id === taskId ? { ...task, status: 'IN_PROGRESS' } : task)));
    } catch (error) {
      console.error('There was an error updating the task status!', error);
    }
  };

  const handlePendingTask = async (taskId) => {
    try {
      await updateTask(taskId, { status: 'PENDING' });
      setTasks(tasks.map(task => (task.id === taskId ? { ...task, status: 'PENDING' } : task)));
    } catch (error) {
      console.error('There was an error updating the task status!', error);
    }
  };

  return (
    <div className="tasks-container">
      <h2>Tasks</h2>
      <div className="task-inputs">
        <label htmlFor="new-task-title">New Task Title</label>
        <input
          id="new-task-title"
          type="text"
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          placeholder="New Task Title"
        />
        <label htmlFor="new-task-description">New Task Description</label>
        <input
          id="new-task-description"
          type="text"
          value={newTask.description}
          onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
          placeholder="New Task Description"
        />
        <label htmlFor="new-task-category">Category</label>
        <select
          id="new-task-category"
          value={newTask.categoryId}
          onChange={(e) => setNewTask({ ...newTask, categoryId: e.target.value })}
        >
          <option value="">Select Category</option>
          {Array.isArray(categories) && categories.map(category => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
        <label htmlFor="new-task-due-date">Due Date</label>
        <input
          id="new-task-due-date"
          type="datetime-local"
          value={newTask.dueDate}
          onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
          placeholder="Due Date"
        />
        <label htmlFor="new-task-priority">Priority</label>
        <select
          id="new-task-priority"
          value={newTask.priority}
          onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
        >
          <option value="LOW">Low</option>
          <option value="MEDIUM">Medium</option>
          <option value="HIGH">High</option>
        </select>
        <label htmlFor="new-task-assignees">Assignees</label>
        <AsyncSelect
          id="new-task-assignees"
          isMulti
          cacheOptions
          loadOptions={loadOptions}
          onChange={(selectedOptions) => setNewTask({ ...newTask, assignees: selectedOptions.map(option => option.value) })}
          placeholder="Select Assignees"
          className="async-select"
        />
        <button onClick={handleCreateTask}>Create Task</button>
      </div>
      <ul className="tasks-list">
        {Array.isArray(tasks) && tasks.map(task => (
          <li key={task.id}>
            {task.title} - {task.description} - {task.status}
            <div>
              <button className="edit" onClick={() => setEditingTask(task)}>Edit</button>
              <button className="delete" onClick={() => handleDeleteTask(task.id)}>Delete</button>
              <button className="pending" onClick={() => handlePendingTask(task.id)} disabled={task.status === 'PENDING'}>Pending</button>
              <button className="in-progress" onClick={() => handleInProgressTask(task.id)} disabled={task.status === 'IN_PROGRESS'}>In Progress</button>
              <button className="complete" onClick={() => handleCompleteTask(task.id)} disabled={task.status === 'COMPLETED'}>Complete</button>
              <button className="comments" onClick={() => setSelectedTaskForComments(task.id)}>Comments</button>
              <button className="reminders" onClick={() => setSelectedTaskForReminders(task.id)}>Reminders</button>
            </div>
          </li>
        ))}
      </ul>
      {editingTask && (
        <div className="edit-task-container">
          <h3>Edit Task</h3>
          <label htmlFor="edit-task-title">Task Title</label>
          <input
            id="edit-task-title"
            type="text"
            value={editingTask.title}
            onChange={(e) => setEditingTask({ ...editingTask, title: e.target.value })}
            placeholder="Task Title"
          />
          <label htmlFor="edit-task-description">Task Description</label>
          <input
            id="edit-task-description"
            type="text"
            value={editingTask.description}
            onChange={(e) => setEditingTask({ ...editingTask, description: e.target.value })}
            placeholder="Task Description"
          />
          <label htmlFor="edit-task-category">Category</label>
          <select
            id="edit-task-category"
            value={editingTask.categoryId}
            onChange={(e) => setEditingTask({ ...editingTask, categoryId: e.target.value })}
          >
            <option value="">Select Category</option>
            {Array.isArray(categories) && categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          <label htmlFor="edit-task-due-date">Due Date</label>
          <input
            id="edit-task-due-date"
            type="datetime-local"
            value={isValidDate(editingTask.dueDate) ? new Date(editingTask.dueDate).toISOString().slice(0, 16) : ''}
            onChange={(e) => setEditingTask({ ...editingTask, dueDate: e.target.value })}
            placeholder="Due Date"
          />
          <label htmlFor="edit-task-priority">Priority</label>
          <select
            id="edit-task-priority"
            value={editingTask.priority}
            onChange={(e) => setEditingTask({ ...editingTask, priority: e.target.value })}
          >
            <option value="LOW">Low</option>
            <option value="MEDIUM">Medium</option>
            <option value="HIGH">High</option>
          </select>
          <label htmlFor="edit-task-assignees">Assignees</label>
          <AsyncSelect
            id="edit-task-assignees"
            isMulti
            cacheOptions
            loadOptions={loadOptions}
            defaultValue={editingTask.assignees.map(assignee => ({ value: assignee, label: users.find(user => user.id === assignee)?.username }))}
            onChange={(selectedOptions) => setEditingTask({ ...editingTask, assignees: selectedOptions.map(option => option.value) })}
            placeholder="Select Assignees"
            className="async-select"
          />
          <button onClick={handleUpdateTask}>Update Task</button>
          <button className="cancel" onClick={() => setEditingTask(null)}>Cancel</button>
        </div>
      )}
      {selectedTaskForComments && (
        <Comment taskId={selectedTaskForComments} username={currentUsername} />
      )}
      {selectedTaskForReminders && (
        <Reminder taskId={selectedTaskForReminders} />
      )}
    </div>
  );
};

export default Tasks;
