import React, { useState, useEffect } from 'react';
import { getCategories, createCategory, deleteCategory } from '../api';

const CategoryManagement = () => {
    const [categories, setCategories] = useState([]);
    const [newCategory, setNewCategory] = useState({ name: '' });
    const [selectedCategoryId, setSelectedCategoryId] = useState(null);

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        try {
            const response = await getCategories();
            setCategories(response.data);
        } catch (error) {
            console.error('Error fetching categories', error);
        }
    };

    const handleCreateCategory = async (e) => {
        e.preventDefault();
        try {
            await createCategory(newCategory);
            setNewCategory({ name: '' });
            fetchCategories();
        } catch (error) {
            console.error('Error creating category', error);
        }
    };

    const handleDeleteCategory = async () => {
        if (!selectedCategoryId) {
            console.error('No category selected');
            return;
        }
        try {
            await deleteCategory(selectedCategoryId);
            setSelectedCategoryId(null);
            fetchCategories();
        } catch (error) {
            console.error('Error deleting category', error);
        }
    };

    return (
        <div>
            <h2>Category Management</h2>
            <form onSubmit={handleCreateCategory}>
                <input
                    type="text"
                    placeholder="Name"
                    value={newCategory.name}
                    onChange={(e) => setNewCategory({ ...newCategory, name: e.target.value })}
                    required
                />
                <button type="submit">Create Category</button>
            </form>
            <ul>
                {categories.map((category) => (
                    <li key={category.categoryId} style={{ backgroundColor: selectedCategoryId === category.categoryId ? 'lightgray' : 'white' }}>
                        <h3>{category.name}</h3>
                        <button onClick={setSelectedCategoryId(category.categoryId)}>Select</button>
                    </li>
                ))}
            </ul>
            <button onClick={handleDeleteCategory} disabled={!selectedCategoryId}>
                Delete Selected Category
            </button>
        </div>
    );
};

export default CategoryManagement;
