import React, { useState, useEffect, useCallback } from 'react';
import { getUser, updateUser } from '../api';
import { useAuth } from '../context/AuthContext';

const Settings = () => {
  const { currentUsername, authToken } = useAuth();
  const [user, setUser] = useState({ notificationsEnabled: false });

  const fetchUserSettings = useCallback(async () => {
    try {
      if (currentUsername && authToken) {
        console.log('Fetching user settings for:', currentUsername);
        const response = await getUser(currentUsername); // Token is automatically added by interceptor
        console.log('User settings fetched:', response.data);
        setUser({
          ...response.data,
          notificationsEnabled: response.data.notificationsEnabled ?? false, // Ensure boolean value
        });
      } else {
        console.log('No currentUsername or authToken');
      }
    } catch (error) {
      console.error('Error fetching user settings', error);
    }
  }, [currentUsername, authToken]);

  useEffect(() => {
    console.log('useEffect - currentUsername:', currentUsername, 'authToken:', authToken);
    fetchUserSettings();
  }, [fetchUserSettings, currentUsername, authToken]);

  const handleToggleNotifications = () => {
    setUser({ ...user, notificationsEnabled: user.notificationsEnabled });
  };

  const handleSaveSettings = async () => {
    try {
      if (currentUsername && authToken) {
        console.log('Saving user settings for:', currentUsername);
        await updateUser(currentUsername, user); // Token is automatically added by interceptor
        alert('Settings saved successfully');
      } else {
        console.log('No currentUsername or authToken');
      }
    } catch (error) {
      console.error('Error saving settings', error);
    }
  };

  return (
    <div>
      <h2>Settings</h2>
      <label>
        Notifications Enabled:
        <input
          type="checkbox"
          checked={!user.notificationsEnabled}
          onChange={handleToggleNotifications}
        />
      </label>
      <button onClick={handleSaveSettings}>Save</button>
    </div>
  );
};

export default Settings;
