// src/elements/Categories.js
import React, { useEffect, useState } from 'react';
import {
  getCategories,
  createCategory,
  updateCategory,
  deleteCategory,
  getCategoryTasks,
  createTask,
  updateTask,
  deleteTask,
  completeTask,
  setTaskReminder,
  getTaskReminders,
  shareTask,
  addTaskComment,
  updateTaskPriority,
  assignTask,
  unassignTask,
} from '../api';

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [newTask, setNewTask] = useState({ title: '', description: '', categoryId: '' });

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await getCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('There was an error fetching the categories!', error);
    }
  };

  const fetchTasksByCategory = async (categoryId) => {
    try {
      const response = await getCategoryTasks(categoryId);
      setTasks(response.data);
    } catch (error) {
      console.error('There was an error fetching the tasks!', error);
    }
  };

  const handleCreateCategory = async () => {
    try {
      const response = await createCategory({ name: newCategoryName });
      setCategories([...categories, response.data]);
      setNewCategoryName('');
    } catch (error) {
      console.error('There was an error creating the category!', error);
    }
  };

  const handleUpdateCategory = async (categoryId, newName) => {
    try {
      const response = await updateCategory(categoryId, { name: newName });
      setCategories(categories.map(cat => (cat.id === categoryId ? response.data : cat)));
    } catch (error) {
      console.error('There was an error updating the category!', error);
    }
  };

  const handleDeleteCategory = async (categoryId) => {
    try {
      await deleteCategory(categoryId);
      setCategories(categories.filter(cat => cat.id !== categoryId));
    } catch (error) {
      console.error('There was an error deleting the category!', error);
    }
  };

  const handleCreateTask = async () => {
    try {
      const response = await createTask(newTask);
      setTasks([...tasks, response.data]);
      setNewTask({ title: '', description: '', categoryId: '' });
    } catch (error) {
      console.error('There was an error creating the task!', error);
    }
  };

  const handleUpdateTask = async (taskId, updatedTask) => {
    try {
      const response = await updateTask(taskId, updatedTask);
      setTasks(tasks.map(task => (task.id === taskId ? response.data : task)));
    } catch (error) {
      console.error('There was an error updating the task!', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId);
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (error) {
      console.error('There was an error deleting the task!', error);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      await completeTask(taskId);
      setTasks(tasks.map(task => (task.id === taskId ? { ...task, completed: true } : task)));
    } catch (error) {
      console.error('There was an error completing the task!', error);
    }
  };

  return (
    <div>
      <h2>Categories</h2>
      <input
        type="text"
        value={newCategoryName}
        onChange={(e) => setNewCategoryName(e.target.value)}
        placeholder="New Category Name"
      />
      <button onClick={handleCreateCategory}>Create Category</button>
      <ul>
        {categories.map(category => (
          <li key={category.id}>
            {category.name}
            <button onClick={() => fetchTasksByCategory(category.id)}>View Tasks</button>
            <button onClick={() => handleUpdateCategory(category.id, prompt('New name:', category.name))}>Edit</button>
            <button onClick={() => handleDeleteCategory(category.id)}>Delete</button>
          </li>
        ))}
      </ul>

      <h2>Tasks</h2>
      <input
        type="text"
        value={newTask.title}
        onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
        placeholder="New Task Title"
      />
      <input
        type="text"
        value={newTask.description}
        onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
        placeholder="New Task Description"
      />
      <select
        value={newTask.categoryId}
        onChange={(e) => setNewTask({ ...newTask, categoryId: e.target.value })}
      >
        <option value="">Select Category</option>
        {categories.map(category => (
          <option key={category.id} value={category.id}>{category.name}</option>
        ))}
      </select>
      <button onClick={handleCreateTask}>Create Task</button>
      <ul>
        {tasks.map(task => (
          <li key={task.id}>
            {task.title} - {task.description}
            <button onClick={() => handleUpdateTask(task.id, { ...task, title: prompt('New title:', task.title) })}>Edit</button>
            <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
            <button onClick={() => handleCompleteTask(task.id)}>Complete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Categories;
