import React, { useEffect, useState } from 'react';
import {
  getCategories,
  createCategory,
  updateCategory,
  deleteCategory,
  getCategoryTasks
} from '../api';

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await getCategories();
      setCategories(response.data || []); // Ensure response.data is an array
    } catch (error) {
      console.error('There was an error fetching the categories!', error);
    }
  };

  const fetchTasksByCategory = async (id) => {
    try {
      const response = await getCategoryTasks(id);
      console.log('Fetched tasks response:', response.data); // Debugging log
      if (Array.isArray(response.data)) {
        setTasks(response.data);
      } else {
        console.error('Unexpected response format:', response.data);
      }
      setSelectedCategory(id);
    } catch (error) {
      console.error('There was an error fetching the tasks!', error);
    }
  };
  
  const handleCreateCategory = async () => {
    try {
      const response = await createCategory({ name: newCategoryName });
      setCategories([...categories, response.data]);
      setNewCategoryName('');
    } catch (error) {
      console.error('There was an error creating the category!', error);
    }
  };

  const handleUpdateCategory = async (id, newName) => {
    try {
      const response = await updateCategory(id, { name: newName });
      setCategories(categories.map(cat => (cat.id === id ? response.data : cat)));
    } catch (error) {
      console.error('There was an error updating the category!', error);
    }
  };

  const handleDeleteCategory = async (id) => {
    try {
      await deleteCategory(id);
      setCategories(categories.filter(cat => cat.id !== id));
    } catch (error) {
      console.error('There was an error deleting the category!', error);
    }
  };

  return (
    <div className="categories-container">
      <h2>Categories</h2>
      <div className="new-category">
        <input
          type="text"
          value={newCategoryName}
          onChange={(e) => setNewCategoryName(e.target.value)}
          placeholder="New Category Name"
        />
        <button onClick={handleCreateCategory}>Create Category</button>
      </div>
      <ul className="categories-list">
        {Array.isArray(categories) && categories.map(category => (
          <li key={category.id} className="category-item">
            <span className="category-name">{category.name}</span>
            <div className="category-actions">
              <button className="category-action-button" onClick={() => fetchTasksByCategory(category.id)}>View Tasks</button>
              <button className="category-action-button" onClick={() => handleUpdateCategory(category.id, prompt('New name:', category.name))}>Edit</button>
              <button className="category-action-button" onClick={() => handleDeleteCategory(category.id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
      {selectedCategory && (
        <div className="tasks-container">
          <h3>Tasks for Category {categories.find(cat => cat.id === selectedCategory)?.name}</h3>
          <ul className="tasks-list">
            {tasks.map(task => (
              <li key={task.id} className="task-item">{task.title}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Categories;
