import React, { useEffect, useState, useCallback } from 'react';
import {
  getTaskComments,
  addTaskComment
} from '../api';
import '../style/Comments.css'; // Assuming you have a CSS file for styling

const Comment = ({ taskId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState({ content: '', author: '' });

  const fetchComments = useCallback(async () => {
    try {
      const response = await getTaskComments(taskId);
      setComments(response.data || []);
    } catch (error) {
      console.error('There was an error fetching the comments!', error);
    }
  }, [taskId]);

  useEffect(() => {
    fetchComments();
  }, [fetchComments]);

  const handleCreateComment = async () => {
    try {
      const response = await addTaskComment(taskId, newComment);
      setComments([...comments, response.data]);
      setNewComment({ content: '', author: '' });
    } catch (error) {
      console.error('There was an error creating the comment!', error);
    }
  };

  return (
    <div className="comment-container">
      <h2>Comments</h2>
      <div className="new-comment">
        <input
          type="text"
          value={newComment.author}
          onChange={(e) => setNewComment({ ...newComment, author: e.target.value })}
          placeholder="Author"
        />
        <textarea
          value={newComment.content}
          onChange={(e) => setNewComment({ ...newComment, content: e.target.value })}
          placeholder="New Comment"
        />
        <button onClick={handleCreateComment}>Add Comment</button>
      </div>
      <ul className="comments-list">
        {comments.map(comment => (
          <li key={comment.id} className="comment-item">
            <span className="comment-author">{comment.author}</span>
            <span className="comment-content">{comment.content}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Comment;
