import axios from 'axios';

const api = axios.create({
  baseURL: '/api',
});

export const createCategory = (category) => api.post('/categories', category);
export const getCategory = (id) => api.get(`/categories/${id}`);
export const getCategories = () => api.get('/categories');
export const updateCategory = (id, category) => api.put(`/categories/${id}`, category);
export const deleteCategory = (id) => api.delete(`/categories/${id}`);
export const getCategoryProgress = (categoryId) => api.get(`/categories/${categoryId}/progress`);
export const getCategoryTasks = (categoryId) => api.get(`/categories/${categoryId}/tasks`);

export const createTask = (task) => api.post('/tasks', task);
export const getTask = (taskId) => api.get(`/tasks/${taskId}`);
export const getTasks = () => api.get('/tasks');
export const updateTask = (taskId, task) => api.put(`/tasks/${taskId}`, task);
export const deleteTask = (taskId) => api.delete(`/tasks/${taskId}`);
export const setTaskReminder = (taskId, reminder) => api.post(`/tasks/${taskId}/reminders`, reminder);
export const getTaskReminders = (taskId) => api.get(`/tasks/${taskId}/reminders`);
export const deleteTaskReminder = (taskId, reminderId) => api.delete(`/tasks/${taskId}/reminders/${reminderId}`);
export const shareTask = (taskId, shareTaskDTO) => api.post(`/tasks/${taskId}/share`, shareTaskDTO);
export const addTaskComment = (taskId, comment) => api.post(`/tasks/${taskId}/comments`, comment);
export const getTaskComments = (taskId) => api.get(`/tasks/${taskId}/comments`);
export const updateTaskPriority = (taskId, priorityUpdateDTO) => api.put(`/tasks/${taskId}/priority`, priorityUpdateDTO);
export const updateTaskStatus = (taskId, status) => api.patch(`/tasks/${taskId}/status`, { status });
export const assignTask = (taskId, task) => api.put(`/tasks/${taskId}/assign`, task);
export const unassignTask = (taskId, task) => api.put(`/tasks/${taskId}/unassign`, task);
export const searchTasks = (params) => api.get('/tasks/search', { params });
export const filterTasks = (params) => api.get('/tasks/filter', { params });
export const getUserTaskSummary = (userId) => api.get(`/tasks/${userId}/summary`);
export const completeTask = (taskId) => api.put(`/tasks/${taskId}/complete`);
export const getTaskSummary = () => api.get('/tasks/summary');
export const updateUserNotificationSettings = (userId, settings) => api.put(`/tasks/${userId}/notification-settings`, settings);
export const registerUser = (user) => api.post('/users/register', user);
export const loginUser = (loginRequest) => api.post('/users/login', loginRequest);
export const getUser = (userId) => api.get(`/users/${userId}`);
export const updateUser = (userId, user) => api.put(`/users/${userId}`, user);
export const deleteUser = (userId) => api.delete(`/users/${userId}`);
export const getUserNotificationSettings = (userId) => api.get(`/users/${userId}/notifications/settings`);
export const setUserNotificationSettings = (userId, enabled) => api.put(`/users/${userId}/notifications/settings`, { enabled });
