import axios from 'axios';

const userApi = axios.create({
  baseURL: 'http://localhost:8081/api',
});

const taskApi = axios.create({
  baseURL: 'http://localhost:8082/api',
});

// Add a request interceptor to include the auth token
userApi.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

taskApi.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export const createCategory = (category) => taskApi.post('/categories', category);
export const getCategory = (id) => taskApi.get(`/categories/${id}`);
export const getCategories = () => taskApi.get('/categories');
export const updateCategory = (id, category) => taskApi.put(`/categories/${id}`, category);
export const deleteCategory = (id) => taskApi.delete(`/categories/${id}`);
export const getCategoryProgress = (id) => taskApi.get(`/categories/${id}/progress`);
export const getCategoryTasks = (id) => taskApi.get(`/categories/${id}/tasks`);

export const createTask = (task) => taskApi.post('/tasks', task);
export const getTask = (taskId) => taskApi.get(`/tasks/${taskId}`);
export const getTasks = () => taskApi.get('/tasks');
export const updateTask = (taskId, task) => taskApi.put(`/tasks/${taskId}`, task);
export const deleteTask = (taskId) => taskApi.delete(`/tasks/${taskId}`);
export const setTaskReminder = (taskId, reminder) => taskApi.post(`/tasks/${taskId}/reminders`, reminder);
export const getTaskReminders = (taskId) => taskApi.get(`/tasks/${taskId}/reminders`);
export const deleteTaskReminder = (taskId, reminderId) => taskApi.delete(`/tasks/${taskId}/reminders/${reminderId}`);
export const shareTask = (taskId, shareTaskDTO) => taskApi.post(`/tasks/${taskId}/share`, shareTaskDTO);
export const addTaskComment = (taskId, comment) => taskApi.post(`/tasks/${taskId}/comments`, comment);
export const getTaskComments = (taskId) => taskApi.get(`/tasks/${taskId}/comments`);
export const updateTaskPriority = (taskId, priorityUpdateDTO) => taskApi.put(`/tasks/${taskId}/priority`, priorityUpdateDTO);
export const updateTaskStatus = (taskId, status) => taskApi.patch(`/tasks/${taskId}/status`, { status });
export const assignTask = (taskId, task) => taskApi.put(`/tasks/${taskId}/assign`, task);
export const unassignTask = (taskId, task) => taskApi.put(`/tasks/${taskId}/unassign`, task);
export const searchTasks = (params) => taskApi.get('/tasks/search', { params });
export const filterTasks = (params) => taskApi.get('/tasks/filter', { params });
export const getUserTaskSummary = (userId) => taskApi.get(`/tasks/${userId}/summary`);
export const completeTask = (taskId) => taskApi.put(`/tasks/${taskId}/complete`);
export const getTaskSummary = () => taskApi.get('/tasks/summary');

export const updateUserNotificationSettings = (userId, settings) => userApi.put(`/tasks/${userId}/notification-settings`, settings);
export const registerUser = (user) => userApi.post('/users/register', user);
export const loginUser = (loginRequest) => userApi.post('/users/login', loginRequest);
export const getUser = (username) => userApi.get(`/users/${username}`);
export const updateUser = (username, user) => userApi.put(`/users/${username}`, user);
export const deleteUser = (username) => userApi.delete(`/users/${username}`);
export const getUserNotificationSettings = (userId) => userApi.get(`/users/${userId}/notifications/settings`);
export const setUserNotificationSettings = (userId, enabled) => userApi.put(`/users/${userId}/notifications/settings`, { enabled });
