import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [authToken, setAuthToken] = useState(null);
  const [currentUsername, setCurrentUsername] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const username = localStorage.getItem('username');
    if (token && username) {
      setAuthToken(token);
      setCurrentUsername(username);
      setIsAuthenticated(true);
    }
  }, []);

  const login = async (loginRequest) => {
    try {
      const response = await axios.post('http://localhost:8081/api/users/login', loginRequest);
      setAuthToken(response.data.token);
      setCurrentUsername(response.data.username);
      setIsAuthenticated(true);
      localStorage.setItem('token', response.data.token); // Store token in localStorage
      localStorage.setItem('username', response.data.username); // Store username in localStorage
      console.log('Login successful:', response.data);
    } catch (error) {
      console.error('Login failed', error);
    }
  };

  const logout = () => {
    setAuthToken(null);
    setCurrentUsername(null);
    setIsAuthenticated(false);
    localStorage.removeItem('token'); // Remove token from localStorage
    localStorage.removeItem('username'); // Remove username from localStorage
    console.log('Logout successful');
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, authToken, currentUsername, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
