import React, { useEffect, useState, useCallback } from 'react';
import {
  getTaskReminders,
  setTaskReminder,
  deleteTaskReminder
} from '../api';
import '../style/Reminders.css'; // Assuming you have a CSS file for styling

const Reminder = ({ taskId }) => {
  const [reminders, setReminders] = useState([]);
  const [newReminderTime, setNewReminderTime] = useState('');

  const fetchReminders = useCallback(async () => {
    try {
      const response = await getTaskReminders(taskId);
      setReminders(response.data || []);
    } catch (error) {
      console.error('There was an error fetching the reminders!', error);
    }
  }, [taskId]);

  useEffect(() => {
    fetchReminders();
  }, [fetchReminders]);

  const handleCreateReminder = async () => {
    try {
      const reminderDate = new Date(newReminderTime);
      const reminderToCreate = {
        reminderTime: [
          reminderDate.getUTCFullYear(),
          reminderDate.getUTCMonth() + 1, // Months are zero-based in JavaScript
          reminderDate.getUTCDate(),
          reminderDate.getUTCHours(),
          reminderDate.getUTCMinutes(),
          reminderDate.getUTCSeconds(),
          reminderDate.getUTCMilliseconds() * 1000 // Convert milliseconds to nanoseconds
        ],
        taskId
      };
      const response = await setTaskReminder(taskId, reminderToCreate);
      setReminders([...reminders, response.data]);
      setNewReminderTime('');
    } catch (error) {
      console.error('There was an error creating the reminder!', error);
    }
  };

  const handleDeleteReminder = async (reminderId) => {
    try {
      await deleteTaskReminder(taskId, reminderId);
      setReminders(reminders.filter(reminder => reminder.id !== reminderId));
    } catch (error) {
      console.error('There was an error deleting the reminder!', error);
    }
  };

  return (
    <div className="reminder-container">
      <h2>Reminders</h2>
      <div className="new-reminder">
        <input
          type="datetime-local"
          value={newReminderTime}
          onChange={(e) => setNewReminderTime(e.target.value)}
          placeholder="New Reminder Time"
        />
        <button onClick={handleCreateReminder}>Create Reminder</button>
      </div>
      <ul className="reminders-list">
        {reminders.map(reminder => (
          <li key={reminder.id} className="reminder-item">
            <span className="reminder-time">
            {new Date(Date.UTC(reminder.reminderTime[0], reminder.reminderTime[1] - 1, reminder.reminderTime[2], reminder.reminderTime[3], reminder.reminderTime[4])).toLocaleString('en-US', {
                                year: 'numeric',
                                day: 'numeric',
                                month: 'long'
                            })}
            </span>
            <button className="reminder-action-button" onClick={() => handleDeleteReminder(reminder.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Reminder;
