import axios from 'axios';

const userApi = axios.create({
  baseURL: 'http://localhost:8081/api/users', // Adjust the base URL to match your backend server
});

export const registerUser = (user) => userApi.post('/register', user);
export const loginUser = (loginRequest) => userApi.post('/login', loginRequest);
export const getUser = (userId) => userApi.get(`/${userId}`);
export const updateUser = (userId, user) => userApi.put(`/${userId}`, user);
export const deleteUser = (userId) => userApi.delete(`/${userId}`);
export const getUserNotificationSettings = (userId) => userApi.get(`/${userId}/notifications/settings`);
export const setUserNotificationSettings = (userId, enabled) => userApi.put(`/${userId}/notifications/settings`, { enabled });
