import React, { useEffect, useState } from 'react';
import {
  createTask,
  getTasks,
  updateTask,
  deleteTask,
  completeTask,
  getCategories,
  getUsers // Assuming you have an API function to get users
} from '../api';
import { useAuth } from '../context/AuthContext';
import { getUser } from '../api'; // Import the getUser API function

const Tasks = () => {
  const { currentUsername } = useAuth();
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [users, setUsers] = useState([]); // State to hold the list of users
  const [newTask, setNewTask] = useState({ title: '', description: '', categoryId: '', dueDate: '', priority: 'LOW', owner: '', assignees: [] });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await getUser(currentUsername);
        setUser(response.data);
      } catch (error) {
        console.error('There was an error fetching the user!', error);
      }
    };

    if (currentUsername) {
      fetchUser();
    }
  }, [currentUsername]);

  useEffect(() => {
    fetchTasks();
    fetchCategories();
    fetchUsers(); // Fetch users when the component mounts
    if (user) {
      setNewTask(prevTask => ({ ...prevTask, owner: user.id })); // Set the owner ID to the logged-in user's ID
    }
  }, [user]);

  const fetchTasks = async () => {
    try {
      const response = await getTasks();
      setTasks(response.data);
    } catch (error) {
      console.error('There was an error fetching the tasks!', error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await getCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('There was an error fetching the categories!', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await getUsers();
      setUsers(response.data);
    } catch (error) {
      console.error('There was an error fetching the users!', error);
    }
  };

  const handleCreateTask = async () => {
    try {
      const response = await createTask(newTask);
      setTasks([...tasks, response.data]);
      setNewTask({ title: '', description: '', categoryId: '', dueDate: '', priority: 'LOW', owner: user.id, assignees: [] });
    } catch (error) {
      console.error('There was an error creating the task!', error);
    }
  };

  const handleUpdateTask = async (taskId, updatedTask) => {
    try {
      const response = await updateTask(taskId, updatedTask);
      setTasks(tasks.map(task => (task.id === taskId ? response.data : task)));
    } catch (error) {
      console.error('There was an error updating the task!', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId);
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (error) {
      console.error('There was an error deleting the task!', error);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      await completeTask(taskId);
      setTasks(tasks.map(task => (task.id === taskId ? { ...task, status: 'COMPLETED' } : task)));
    } catch (error) {
      console.error('There was an error completing the task!', error);
    }
  };

  return (
    <div>
      <h2>Tasks</h2>
      <input
        type="text"
        value={newTask.title}
        onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
        placeholder="New Task Title"
      />
      <input
        type="text"
        value={newTask.description}
        onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
        placeholder="New Task Description"
      />
      <select
        value={newTask.categoryId}
        onChange={(e) => setNewTask({ ...newTask, categoryId: e.target.value })}
      >
        <option value="">Select Category</option>
        {categories.map(category => (
          <option key={category.id} value={category.id}>
            {category.name}
          </option>
        ))}
      </select>
      <input
        type="datetime-local"
        value={newTask.dueDate}
        onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
        placeholder="Due Date"
      />
      <select
        value={newTask.priority}
        onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
      >
        <option value="LOW">Low</option>
        <option value="MEDIUM">Medium</option>
        <option value="HIGH">High</option>
      </select>
      <select
        value={newTask.assignees[0] || ''} // Assuming single assignee for simplicity
        onChange={(e) => setNewTask({ ...newTask, assignees: [e.target.value] })}
      >
        <option value="">Select Assignee</option>
        {users.map(user => (
          <option key={user.id} value={user.id}>
            {user.username}
          </option>
        ))}
      </select>
      <button onClick={handleCreateTask}>Create Task</button>
      <ul>
        {tasks.map(task => (
          <li key={task.id}>
            {task.title} - {task.description} - {task.status}
            <button onClick={() => handleUpdateTask(task.id, { ...task, title: prompt('New title:', task.title) })}>Edit</button>
            <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
            <button onClick={() => handleCompleteTask(task.id)}>Complete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tasks;
